package com.mavha.test.backend.repository;

import static org.junit.Assert.assertEquals;

import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.context.junit4.SpringRunner;

import com.mavha.test.backend.model.Todo;

@RunWith(SpringRunner.class)
@SpringBootTest
public class TodoRepositoryTest {

    @Autowired
    private TodoRepository todoRepository;

    @Test
    public void whenFindByName_thenReturnEmployee() {
        // given
        Todo alex = new Todo();
        alex.setDescripcion("Prepare presentation1");
        alex.setEstado("pendiente");
        //"");

        todoRepository.save(alex);
     
        // when
        Todo found = todoRepository.findByDescripcion(alex.getDescripcion());
     
        // then
        assertEquals("descripcion", alex.getDescripcion(),found.getDescripcion());
    }

}
